const houses = ['Serpentard', 'Griffondor', 'Serdaigle', 'Pouffsoufle',];
const Reducer = (state, action) => ([
    {houses,
    selectedHouses: houses[Math.floor(Math.random() * 4)]}
])

export default Reducer;
